mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_ID=#{memberId}
AND MEMBER_STATUS='Y'

SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_ID=#{memberId}
AND MEMBER_STATUS='Y'

SELECT (SELECT MEMBER_NM FROM MEMBER WHERE MEMBER_NO IN (SELECT TRAINER_NO FROM PAYMENT WHERE MEMBER_NO=#{memberNo}))as MEMBER_NM,MERCHANT_UID,CLASS_NM,AMOUNT,PAYMENT_DATE,CLASS_CNT, PAYMENT.TRAINER_NO 
FROM PAYMENT JOIN CLASS_STATUS USING(MEMBER_NO) JOIN MEMBER USING(MEMBER_NO)
WHERE MEMBER_NO=#{memberNo}
